import React from "react";
import { MdOutlineEmail } from "react-icons/md";
import { RiMessengerLine } from "react-icons/ri";
import { BsWhatsapp } from "react-icons/bs";
import "../../src/contact.css";
import { useRef } from "react";
import emailjs from "emailjs-com";
import { ToastContainer, toast } from "react-toastify";
// import "react-toastify/dist/ReactToastify.css";
function Contact() {
  const form = useRef();
  const sendEmail = (e) => {
    e.preventDefault();
    // toast.success("Message Sent!", {
    //   position: toast.POSITION.TOP_RIGHT,
    // });
    // emailjs
    //   .sendForm(
    //     "service_v393eje",
    //     "template_y5s7k3f",
    //     form.current,
    //     "snHYHsWQTGs2LCqJj"
    //   )
    //   .then(
    //     (result) => {
    //       console.log(result.text);
    //     },
    //     (error) => {
    //       console.log(error.text);
    //     }
    //   );

    e.target.reset();
  };
  return (
    <section id="contact">
      <h5>Get In Touch</h5>
      <h2>Contact</h2>
      <div className="container contact_container">
        <div className="contact_options">
          <article className="contact_option">
            <MdOutlineEmail className="contact_option-icon" />
            <h4>Email</h4>
            <h5 className="myemail">muhammadnomantariq1999@gmail.com</h5>
            <a
              href="mailto:muhammadnomantariq1999@gmail.com?subject=Hello Noman!I want chat with you..&body=Great Work!!%20Impressed with your portfolio.."
              target="_blank"
              rel="noopener noreferrer"
            >
              Send me an email
            </a>
          </article>
          <article className="contact_option">
            <RiMessengerLine className="contact_option-icon" />
            <h4>Messenger</h4>
            <h5>Mian Noman</h5>
            <a
              href="https://m.me/main.noman.1/"
              target="_blank"
              rel="noopener noreferrer"
            >
              Send a message
            </a>
          </article>
          <article className="contact_option">
            <BsWhatsapp className="contact_option-icon" />
            <h4>WhatsApp</h4>
            <h5>+92300-8033597</h5>
            <a
              href="https://api.whatsapp.com/send?phone=+923008033597&text=Hello%2C%20Noman%20I%20want%20chat%20with%20you!"
              target="_blank"
              rel="noopener noreferrer"
            >
              Send a message
            </a>
          </article>
        </div>
        <form ref={form} onSubmit={sendEmail}>
          <input type="text" name="name" placeholder="Your Full Name" />
          <input type="email" name="email" placeholder="Your Email" required />
          <textarea
            name="message"
            rows="7"
            placeholder="Your Message"
            required
          ></textarea>
          <button type="submit" className="btn btn-primary">
            Send Message
          </button>
        </form>
      </div>
    </section>
  );
}

export default Contact;
